(function(){'use strict';var languages=[];window.setupLanguages=setupLanguages;window.activateLanguage=activateLanguage;window.getLanguageFromQueryString=getLanguageFromQueryString;function activateLanguage(language){if(!language)return;if(language==="")return;$(".lang-selector a").removeClass('active');$(".lang-selector a[data-language-name='"+language+"']").addClass('active');for(var i=0;i<languages.length;i++){$(".highlight.tab-"+languages[i]).hide();$(".lang-specific."+languages[i]).hide()}
    $(".highlight.tab-"+language).show();$(".lang-specific."+language).show();window.recacheHeights();if($(window.location.hash).get(0)){$(window.location.hash).get(0).scrollIntoView(!0)}}
    function parseURL(str){if(typeof str!=='string'){return{}}
        str=str.trim().replace(/^(\?|#|&)/,'');if(!str){return{}}
        return str.split('&').reduce(function(ret,param){var parts=param.replace(/\+/g,' ').split('=');var key=parts[0];var val=parts[1];key=decodeURIComponent(key);val=val===undefined?null:decodeURIComponent(val);if(!ret.hasOwnProperty(key)){ret[key]=val}else if(Array.isArray(ret[key])){ret[key].push(val)}else{ret[key]=[ret[key],val]}
            return ret},{})};function stringifyURL(obj){return obj?Object.keys(obj).sort().map(function(key){var val=obj[key];if(Array.isArray(val)){return val.sort().map(function(val2){return encodeURIComponent(key)+'='+encodeURIComponent(val2)}).join('&')}
        return encodeURIComponent(key)+'='+encodeURIComponent(val)}).join('&'):''};function getLanguageFromQueryString(){if(location.search.length>=1){var language=parseURL(location.search).language;if(language){return language}else if(jQuery.inArray(location.search.substr(1),languages)!=-1){return location.search.substr(1)}}
        return!1}
    function generateNewQueryString(language){var url=parseURL(location.search);if(url.language){url.language=language;return stringifyURL(url)}
        return language}
    function pushURL(language){if(!history){return}
        var hash=window.location.hash;if(hash){hash=hash.replace(/^#+/,'')}
        history.pushState({},'','?'+generateNewQueryString(language)+'#'+hash);localStorage.setItem("language",language)}
    function setupLanguages(l){var defaultLanguage=localStorage.getItem("language");languages=l;var presetLanguage=getLanguageFromQueryString();if(presetLanguage){activateLanguage(presetLanguage);localStorage.setItem("language",presetLanguage)}else if((defaultLanguage!==null)&&(jQuery.inArray(defaultLanguage,languages)!=-1)){activateLanguage(defaultLanguage)}else{activateLanguage(languages[0])}}
    $(function(){$(".lang-selector a").on("click",function(){var language=$(this).data("language-name");pushURL(language);activateLanguage(language);return!1})})})()